<script setup>
const { name, label } = defineProps(["name", "label", "checked"]);

const emit = defineEmits(["onChange"]);

const emitUpdate = (value) => {
  emit("onChange", value);
};
</script>

<template>
  <div class="flex items-center gap-2">
    <label :for="name" class="capitalize cursor-pointer select-none"
      >{{ label }}
    </label>
    <input
      class="peer relative border-gray-full h-4 w-4 shrink-0 appearance-none rounded-sm border after:absolute after:left-0 after:top-0 after:h-full after:w-full after:bg-[url('data:image/svg+xml;base64,')] after:bg-[length:30px] after:bg-center after:bg-no-repeat after:content-[''] checked:bg-green-500 hover:ring hover:ring-transparent focus:outline-none"
      type="checkbox"
      :checked="checked"
      :id="name"
      @change="emitUpdate($event.target.checked)"
    />
  </div>
</template>

<style scoped>
.container input:checked ~ .checkmark {
  background-color: #00c250;
}

.checkmark:after {
  content: "";
  position: absolute;
  display: none;
}

.container input:checked ~ .checkmark:after {
  display: block;
}

.container .checkmark:after {
  left: 4px;
  top: 1px;
  width: 6px;
  height: 10px;
  border: solid white;
  border-width: 0 3px 3px 0;
  -webkit-transform: rotate(45deg);
  -ms-transform: rotate(45deg);
  transform: rotate(45deg);
}
</style>
